import React from "react";

export const ContactPicker = ({ contactsArray, handleChange, value, name }) => {
  return (
    <>
      <label htmlFor="contact">Select Contact</label>
      <select id="contact" onChange={(e)=>handleChange(e.target.value, 'contact')} name={name} value={value} defaultValue=''>
        <option value="" selected>
          No Contact Selected
        </option>
        { contactsArray.map((contact) => (
          <option value={contact.name}>{contact.name}</option>
        ))}
      </select>
    </>
  );
};
