import React, { useState } from "react";
import styles from "./userInput.module.css";

function UserInput(props) {
  const [userInfo, setUserInfo] = useState({});

  const inputChangeHandler = (id, value) => {
    setUserInfo((prev) =>{return {...prev,  [id]: value}});
  };

  const formSubmitHandler = (e) => {
    e.preventDefault();
    const newUser = {userName: userInfo.userName, age: userInfo.age}
    console.log(newUser);
    props.onCreateUser(newUser);
setUserInfo({userName:'', age: ''})
  };

  return (
    <div className={styles["user-input"]}>
        <h3>Add new user</h3>
      <form onSubmit={formSubmitHandler} action="">
        <div>
          <label htmlFor="userName">Username</label>
          <input
          value={userInfo.userName}
            onChange={(e) => inputChangeHandler("userName", e.target.value)}
            type="text"
            id="userName"
          />
        </div>
        <div>
          <label htmlFor="age">Age</label>
          <input
          value={userInfo.age}
            onChange={(e) => inputChangeHandler("age", e.target.value)}
            type="number"
            id="age"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default UserInput;
