import { useState } from "react";

import Header from "./components/Header";
import UserInput from "./components/UserInput";
import UserList from "./components/UserList";

function App() {
  const [validName, setValidName] = useState(true);
  const [validAge, setValidAge] = useState(true);
  const [userList, setUserList] = useState([]);
const [createdUser, setCreatedUser] = useState(null)


  const displayUser = (newUser) => {


    const userName = newUser.userName;
    const age = newUser.age;

    setCreatedUser({name: userName, age: +age})


    if (userName.trim().length === 0) {
      setValidName(false);
      return;
    }
    if (Number(age) < 0 || age === '') {
      setValidAge(false);
      return;
    }
    const user = { "user-name": userName, "user-age": +age };
console.log(user);

    setUserList((prev)=>[user, ...prev]);

  };

 const allValid = validAge && validName

  return (
    <div>
      <Header />
      <UserInput onCreateUser={displayUser} />
       {allValid &&<UserList users = {userList}/>}
       {!validAge && <p>age can not be {createdUser.age}</p>}
       {!validName && <p>invalid username</p>}

    </div>
  );
}

export default App;
